import { isContext } from "node:vm";
import { MachineConfig, send, Action, assign } from "xstate";
import { nluRequest } from "./index"

function say(text: string): Action<SDSContext, SDSEvent> {
    return send((_context: SDSContext) => ({ type: "SPEAK", value: text }))
}

function listen(): Action<SDSContext, SDSEvent> {
    return send('LISTEN')
}

function rasaParse(target1: string, intent1: string, target2: string, intent2: string): MachineConfig<SDSContext, any, SDSEvent> {
    return ({
        invoke: {
            id: 'rasa',
            src: (context, event) => nluRequest(context.recResult),
            onDone: [
                { target: `#root.dm.game.${target1}`, cond: (context, event) => event.data.intent["name"] === intent1 },
                { target: `#root.dm.game.${target2}`, cond: (context, event) => event.data.intent["name"] === intent2 },
                { target: "#root.dm.error", cond: (context, event) => event.data.intent["name"] !== intent1 || event.data.intent["name"] !== intent2 },
                { target: "#root.dm.game.hist", cond: (context, event) => event.data.intent["name"] === "repeat" },                    
                { target: "#root.dm.help", cond: (context, event) => event.data.intent["name"] === "help" },
                { target: "#root.dm.quit", cond: (context, event) => event.data.intent["name"] === "quit" }],
            onError: {
                    target: '#root.dm.error',
                }
        }
    })
}

function repeatQuestion(currentState: string, question: string, target1: string, intent1: string, target2: string, intent2: string): MachineConfig<SDSContext, any, SDSEvent> {
    return ({
        initial: "prompt",
        on: {
            RECOGNISED: {
                target: ".rasa",
            }
        },
        states: {
            prompt: {
                entry: say(question),
                on: { ENDSPEECH: "ask" }
            },
            ask: {
                entry: listen()
            },
            rasa: {
                invoke: {
                    id: 'rasa',
                    src: (context, event) => nluRequest(context.recResult),
                    onDone: [
                        { target: `#root.dm.game.${target1}`, cond: (context, event) => event.data.intent["name"] === intent1 },
                        { target: `#root.dm.game.${target2}`, cond: (context, event) => event.data.intent["name"] === intent2 },
                        { target: `#root.dm.game.${currentState}.repeat`, cond: (context, event) => event.data.intent["name"] !== intent1 || event.data.intent["name"] !== intent2 },
                        { target: "#root.dm.help", cond: (context, event) => event.data.intent["name"] === "help" },
                        { target: "#root.dm.quit", cond: (context, event) => event.data.intent["name"] === "quit" }],
                    onError: {
                        target: "#root.dm.error",
                    }
                }
            }
        }
    })
}


